/**
 * Initializes the blox config data
 * @param {*} config
 */
const initialize = (config) => {
  if (config == null) {
    throw new Error("Appblox intitialiation failed! No config passed");
  }else if (config.clientId && config.clientSecret) {
    global.BloxConfig = config;
    return console.log("Appblox initialized");
  }
  
  throw new Error("Appblox intitialiation failed! Invalid config passed");
};

export default { initialize };
